function getCreativePrice($price, $payments){
    if($price == NULL || $price == ''){
            $price = 'Lead Generation';
    }
    else if($payments == 'Infinite'){
        $price  = $price;
    }else{
        $price = $payments.' of $'. $price;
    }
    
    return $price;
}

