  //  $sql = "SELECT group_id FROM summary_short s INNER JOIN summary_network n ON s.parent_id = n.adv_id WHERE n.network_code = '".$network_code."' AND s.parent_id = ".$adv_id." AND s.parent_type = 'adv' GROUP BY parent_id";
