function isLoginSessionExpired() {
    $login_session_duration = 10; 
    $current_time = time(); 
    if(isset($_SESSION['loggedin_time']) and isset($_SESSION["user_id"])){  
        if(((time() - $_SESSION['loggedin_time']) > $login_session_duration)){ 
            return true; 
        } else{
                    $_SESSION['loggedin_time']= time();
                    return false;
                }
    }else{
            return true;
        }
   // return false;
}


