            $rosDay     = substr($rosDay[0],0,1).'-'.substr($rosDay[1],0,1);
        }else{
            $rosDay     = '';
        }
        if(isset($requestData['dp_time']) && !empty($requestData['dp_time'])){
            $rosTime    = urldecode($requestData['dp_time']);
