    } elseif ($requestData['report_type'] == 'airing_detail') {
        $rows  = get_query_result('__query_excel_export_airing', $params_excel_export, 'FETCH_ASSOC');
    }
  
    if (count($rows) < 1) {
        $no_of_rows = 0;
    } else {
