function excelReportGirdList(){
    $export = 1;
    $requestData = array();   

    $requestData['c']                   = $c = urldecode($_REQUEST['c']);
    $requestData['tab']                 =  $_REQUEST['tab'];
    $requestData['val']                 =  $_REQUEST['val'];
    $requestData['sd']                  =  $_REQUEST['sd'];
    $requestData['ed']                  =  $_REQUEST['ed'];
