    $spendCreativeColHeading = 'Spend By Creative ($)';

    $active_flag_arr = ['-1', '2'];
    if($requestData['type'] == "advertiser"){        //for brand
      
        $requestData['adv_id']  =  $_REQUEST['adv_id'];        
        $data_list = advBrandsList($export,$requestData);
        $resultSet = [];
