        $rank = $index = $b_rank = 0;
        foreach($result as $k => $v){
            $v = (object) $v;
            if($index == 0){
                $b_rank    = ++$rank;
            }else if(isset($result[$index - 1]) && $result[$index - 1]->brand_name != $v->brand_name ) {
                $b_rank    = ++$rank;
            }

