}


function getCompanyOwners() {
    $db                                 = getConnection(); 
    try {
        $obj        = get_query_result('__query_get_company_owners', array(), 'FETCH_OBJ');

        echo json_encode(array('status'=>1, 'result' => $obj )); 
    }
    catch(PDOException $e) {
        echo '{"status":0,"error":{"text":'. $e->getMessage() .'}}'; 
    }
}
